package Aula7;

public class OperadoresAritmeticos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int resultado = 0;
		resultado = 10 + 5;
		System.out.println(resultado);
		
		resultado = resultado + 5;
		System.out.println(resultado);
		
		resultado = resultado / 2 ;
		System.out.println(resultado);
		

	}

}
